          const retornoEventos = await api.get(eventsResource);
          setEventos(retornoEventos.data);
          console.log(retornoEventos.data);
        } catch (error) {
          console.log("Erro na api");
          console.log(error);
        }
      } else {
        //chamar a api dos meus eventos

        try {
          const retornoEventos = await api.get(
